import React from 'react';

const Header = () => {
  return (
    <header style={styles.header}>
      <a href="#">
      <div style={styles.logo}>Funny Mind</div>
      </a>
      <nav style={styles.nav}>
        <a href="#" style={styles.button}>Sobre Nosotros</a>
        <a href="#" style={styles.button}>Contáctenos</a>
      </nav>
    </header>
  );
};

const styles = {
  a: {
    textDecoration: none 
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#20B2AA', // azul agua marina
    color: '#fff',
    padding: '1rem 2rem',
  },
  logo: {
    color: 'white',
    textDecoration: 'none !important',
    fontSize: '1.8rem',
    fontWeight: 'bold',
    fontFamily: 'Arial, sans-serif',
  },
  nav: {
    display: 'flex',
    gap: '1.5rem', // más separación entre los botones
  },
  button: {
    display: 'inline-block',
    padding: '0.7rem 1.2rem',
    backgroundColor: 'transparent', // Fondo transparente
    color: '#fff',
    border: '2px solid #fff', // Borde blanco sutil
    borderRadius: '8px',
    textDecoration: 'none',
    fontWeight: '500',
    transition: 'background-color 0.3s ease, color 0.3s ease',
    fontSize: '1rem',
  },
};


export default Header;
